CREATE TABLE `btx_dogwood_authors` (`id` int(11) NOT NULL AUTO_INCREMENT, `user` int(11) unsigned DEFAULT NULL, `name` varchar(255) NOT NULL DEFAULT '', `title` varchar(255) NOT NULL DEFAULT '', `biography` text NOT NULL, `image` varchar(255) NOT NULL DEFAULT '', `route` varchar(255) NOT NULL DEFAULT '', `position` int(11) NOT NULL, PRIMARY KEY (`id`), KEY `route` (`route`), KEY `position` (`position`), KEY `user` (`user`), CONSTRAINT `btx_dogwood_authors_ibfk_1` FOREIGN KEY (`user`) REFERENCES `bigtree_users` (`id`) ON DELETE SET NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

CREATE TABLE `btx_dogwood_categories` (`id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(255) NOT NULL DEFAULT '', `route` varchar(255) NOT NULL DEFAULT '', `position` int(11) NOT NULL, PRIMARY KEY (`id`), KEY `position` (`position`), KEY `route` (`route`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

CREATE TABLE `btx_dogwood_posts` (`id` int(11) NOT NULL AUTO_INCREMENT, `author` int(11) NOT NULL, `date` datetime DEFAULT NULL, `title` varchar(255) NOT NULL DEFAULT '', `blurb` text NOT NULL, `content` longtext NOT NULL, `image` varchar(255) NOT NULL DEFAULT '', `caption` varchar(255) DEFAULT NULL, `external_link` varchar(255) NOT NULL DEFAULT '', `publish_date` datetime DEFAULT NULL, `route` varchar(255) NOT NULL DEFAULT '', `featured` char(2) DEFAULT NULL, PRIMARY KEY (`id`), KEY `author` (`author`), KEY `date` (`date`), KEY `featured` (`featured`), KEY `publish_date` (`publish_date`), CONSTRAINT `btx_dogwood_posts_ibfk_1` FOREIGN KEY (`author`) REFERENCES `btx_dogwood_authors` (`id`) ON DELETE CASCADE) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

CREATE TABLE `btx_dogwood_post_categories` (`id` int(11) NOT NULL AUTO_INCREMENT, `post` int(11) DEFAULT NULL, `category` int(11) DEFAULT NULL, PRIMARY KEY (`id`), KEY `post` (`post`), KEY `category` (`category`), CONSTRAINT `btx_dogwood_post_categories_ibfk_2` FOREIGN KEY (`category`) REFERENCES `btx_dogwood_categories` (`id`) ON DELETE CASCADE, CONSTRAINT `btx_dogwood_post_categories_ibfk_1` FOREIGN KEY (`post`) REFERENCES `btx_dogwood_posts` (`id`) ON DELETE CASCADE) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

CREATE TABLE `sample_features` (`id` int(11) unsigned NOT NULL AUTO_INCREMENT, `image` varchar(255) NOT NULL DEFAULT '', `background` varchar(255) NOT NULL DEFAULT '', `title` varchar(255) NOT NULL DEFAULT '', `description` varchar(255) NOT NULL DEFAULT '', `link` varchar(255) NOT NULL DEFAULT '', `image_credit` varchar(255) NOT NULL DEFAULT '', `image_link` varchar(255) NOT NULL DEFAULT '', `position` int(11) NOT NULL, `approved` char(2) NOT NULL DEFAULT '', PRIMARY KEY (`id`), KEY `position` (`position`), KEY `approved` (`approved`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

CREATE TABLE `sample_glossary` (`id` int(11) unsigned NOT NULL AUTO_INCREMENT, `term` varchar(255) NOT NULL DEFAULT '', `route` varchar(255) NOT NULL DEFAULT '', `description` text NOT NULL, `approved` char(2) NOT NULL DEFAULT '', PRIMARY KEY (`id`), KEY `route` (`route`), KEY `approved` (`approved`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;
