INSERT INTO `bigtree_pages` (`id`, `trunk`, `parent`, `in_nav`, `nav_title`, `route`, `path`, `title`, `meta_keywords`, `meta_description`, `template`, `external`, `new_window`, `resources`, `callouts`, `archived`, `archived_inherited`, `publish_at`, `expire_at`, `max_age`, `last_edited_by`, `ga_page_views`, `position`, `created_at`, `updated_at`) VALUES (1, '', -1, '', 'Trees of Large Sizes', '', '', 'Trees of Large Sizes', '', '', 'home', '', '', '[]', '[{\"title\":\"Mr. T Can Chop Down 20 Trees Per Hour\",\"image\":\"{staticroot}files\\/pages\\/wood_grain.jpg\",\"caption\":\"By Jos., GFDL or CC-BY-3.0, via Wikimedia Commons\",\"description\":\"<p>In July 1976, Tureaud\'s platoon sergeant punished him by giving him the detail of chopping down trees during training camp at Fort McCoy in Wisconsin, but did not tell him how many trees, so Tureaud single-handedly chopped down over seventy trees from 6:30 am to 10:00 am, until a shocked major superseded the sergeant\'s orders.<\\/p>\\r\\n<p>Later in life, Tureaud angered the residents of a Chicago suburb called Lake Forest, by cutting down more than a hundred oak trees on his estate. The incident is now referred to as \\\"The Lake Forest Chain Saw Massacre\\\"<\\/p>\",\"link\":\"http:\\/\\/en.wikipedia.org\\/wiki\\/Mr._T\",\"link_title\":\"Read More on Wikipedia\",\"type\":\"flexible-callout\",\"display_title\":\"Mr. T Can Chop Down 20 Trees Per Hour\"}]', '', '', NULL, NULL, 0, 1, 0, 1, '0000-00-00 00:00:00', '2012-10-31 11:13:10'), (2, '', 0, 'on', 'Tree Blog', 'tree-blog', 'tree-blog', 'Tree Blog', '', '', 'btx-dogwood-blog', '', '', '[]', '[]', '', '', NULL, NULL, 0, 1, 0, 0, '2012-06-20 15:12:14', '2012-06-25 12:06:56'), (3, '', 0, 'on', 'Glossary', 'glossary', 'glossary', 'Glossary', '', '', 'glossary', '', '', '{\"page_header\":\"Glossary of Tree Terminology\",\"page_content\":\"<p><em>Source: <a href=\\\"http:\\/\\/www.arborday.org\\/trees\\/treeguide\\/glossary.cfm\\\" target=\\\"_blank\\\">Arbor Day Foundation<\\/a><\\/em><\\/p>\"}', '[]', '', '', NULL, NULL, 0, 1, 0, 0, '2012-06-20 15:12:54', '2012-07-10 13:12:20'), (4, '', 0, 'on', 'About', 'about', 'about', 'About', '', '', 'content', '', '', '{\"page_header\":\"About the BigTree Sample Site\",\"page_content\":\"<p>This site is distributed as a technical demonstration of the open source software product BigTree CMS. All content and photographs are sourced from Wikipedia and Wikimedia Commons and are subject to the distribution rights of their respected licenses. We are not tree experts nor do we intend for this site to be used as a factual reference. The design elements and code used to make this site are licensed under the LGPL license.<\\/p>\\r\\n<h2>Fonts<\\/h2>\\r\\n<h4>Open Sans<\\/h4>\\r\\n<div class=\\\"description\\\">\\r\\n<p>Open Sans is a humanist sans serif typeface designed by Steve Matteson, Type Director of Ascender Corp. This version contains the complete 897 character set, which includes the standard ISO Latin 1, Latin CE, Greek and Cyrillic character sets. Open Sans was designed with an upright stress, open forms and a neutral, yet friendly appearance. It was optimized for print, web, and mobile interfaces, and has excellent legibility characteristics in its letterforms.<br \\/><a href=\\\"https:\\/\\/www.google.com\\/webfonts\\/specimen\\/Open+Sans\\\" target=\\\"_blank\\\">Download Open Sans<\\/a><\\/p>\\r\\n<h4>Gentium Basic<\\/h4>\\r\\n<p>Gentium Basic and Gentium Book Basic are font families based on the original Gentium design, but with additional weights. The \\\"Book\\\" family is slightly heavier. Both families come with a complete regular, bold, italic and bold italic set of fonts.<br \\/><a href=\\\"https:\\/\\/www.google.com\\/webfonts\\/specimen\\/Gentium+Basic\\\" target=\\\"_blank\\\">Download Gentium Basic<\\/a><\\/p>\\r\\n<\\/div>\",\"photo_file\":\"\",\"photo_caption\":\"\"}', '[{\"title\":\"What Is BigTree?\",\"image\":\"{staticroot}files\\/pages\\/bigtree_icon.png\",\"caption\":\