INSERT INTO `sample_features` (`id`, `image`, `background`, `title`, `description`, `link`, `image_credit`, `image_link`, `position`, `approved`) VALUES (1, '{staticroot}images/features/sequoia_sempervirens_bigsur.jpg', '003333', 'Sequoia', 'Sequoia is a genus of redwood coniferous trees in the Sequoioideae subfamily, of the Cupressaceae family. The only extant species of the genus is the Sequoia sempervirens in the Northern California coastal forests ecoregion of Northern California and Sout', 'http://en.wikipedia.org/wiki/Sequoia_%28genus%29', 'Giles Douglas, CC-BY-2.0, via Wikimedia Commons', 'http://commons.wikimedia.org/wiki/File%3ASequoia_sempervirens_BigSur.jpg', 4, 'on'), (2, '{staticroot}images/features/linde_von_linn.jpg', '333333', 'Tilia Cordata', 'Integer posuere erat a ante venenatis dapibus posuere velit aliquet. Etiam porta sem malesuada magna mollis euismod. Curabitur blandit tempus porttitor.', 'http://en.wikipedia.org/wiki/Tilia_cordata', 'Stefan Wernli, CC-BY-SA-2.5, via Wikimedia Commons', 'http://commons.wikimedia.org/wiki/File:Linde_von_linn.jpg', 2, 'on'), (3, '{staticroot}images/features/cherry_tree_blossoms.jpg', '333366', 'Prunus Serrulata', 'Prunus serrulata or Japanese Cherry; also called Hill Cherry, Oriental Cherry or East Asian Cherry, is a species of cherry native to Japan, Korea and China. It is known for its spring cherry blossom displays and festivals.', 'http://en.wikipedia.org/wiki/Prunus_serrulata', 'Agricultural Research Service, Public Domain', 'http://commons.wikimedia.org/wiki/File:Cherry_tree_blossoms.jpg', 3, 'on');
INSERT INTO `sample_glossary` (`id`, `term`, `route`, `description`, `approved`) VALUES (1, 'acid', 'acid', 'pH - acidity or alkalinity ranging from 3 (strongly acid) to 11 (strongly alkaline) with 7 being neutral.', 'on'), (2, 'alkaline', 'alkaline', 'pH - acidity or alkalinity ranging from 3 (strongly acid) to 11 (strongly alkaline) with 7 being neutral.', 'on'), (3, 'alleopathy', 'alleopathy', 'The suppression of growth of one plant species by another due to the release of toxic substances.', 'on'), (4, 'anthracnose', 'anthracnose', 'A group of fungi that cause dieback and sometimes death to various species, such as dogwoods, sycamores, oaks, and maples.', 'on'), (5, 'alternate', 'alternate', 'Leaves that are staggered, not placed directly across from each other on the twig.', 'on'), (6, 'blade', 'blade', 'The flat part of a leaf or leaflet, characteristic of broadleaf trees.', 'on'), (7, 'bract', 'bract', 'A modified leaf that bears a flower.', 'on'), (8, 'broadleaf', 'broadleaf', 'A tree with leaves that are flat and thin, and generally shed annually.', 'on'), (9, 'bud scar', 'bud scar', 'the marks remaining after bud scales drop in the spring.', 'on'), (10, 'clingstone', 'clingstone', 'any of various stone fruits (as some peaches or plums) with flesh that adheres strongly to the pit.', 'on'), (11, 'compound leaf', 'compound-leaf', 'a leaf with more than one blade. All blades are attached to a single leafstem. Where the leafstem attaches to the twig, there is a bud.', 'on'), (12, 'conifer', 'conifer', 'A cone-bearing tree.', 'on'), (13, 'cross-pollination', 'cross-pollination', 'fertilization between genetically compatible trees for better fruit, often resulting in superior offspring.', 'on'), (14, 'crown', 'crown', 'The head of foliage of a tree or shrub -- this is the form or shape of the tree.', 'on'), (15, 'deciduous', 'deciduous', 'Shedding all leaves annually.', 'on'), (16, 'entire', 'entire', 'A leaf margin with smooth, untoothed edges.', 'on'), (17, 'evergreen', 'evergreen', 'Trees with needles or leaves that remain alive and on the tree through the winter and into the next growing season.', 'on'), (18, 'exfoliate', 'exfoliate', 'peeling in shreds or thin layers, as bark from a tree.', 'on'), (19, 'freestone', 'freestone', 'A fruit stone to which the flesh does not cling.', 'on'), (20, 'habit', 'habit', 'The general mode of plant growth. Used to describe the overall shape of a tree.', 'on'), (21, 'hardiness zone', 'hardiness-zone', 'A plant can be expected to grow in the zone\'s temperature extremes, as determined by the lowest annual temperature. Other conditions such as moisture, soil, and wind might affect the availability of individual plants.', 'on'), (22, 'knees', 'knees', 'The tree trunk in wet conditions exhibits a broad buttress with protrusions from the roots.', 'on'), (23, 'leaf scar', 'leaf scar', 'The mark left on the twig where the leaf was previously attached.', 'on'), (24, 'lobes', 'lobes', 'Projections that shape a leaf.', 'on'), (25, 'margin', 'margin', 'The edge of a leaf.', 'on'), (26, 'midrib', 'midrib', 'The primary rib or central vein of a leaf.', 'on'), (27, 'native', 'native', 'Inherent and original to a geographic area.', 'on'), (28, 'opposite', 'opposite', 'Two or three leaves that are directly across from each other on the same twig.', 'on'), (29, 'palmate', 'palmate', 'Blades or lobes or veins of the leaf arranged like fingers on the palm of a hand.', 'on'), (30, 'persistent', 'persistent', 'Deciduous leaf blades that remain on the tree for more than a year.', 'on'), (31, 'petiole', 'petiole', 'The leafstalk that connects the blade(s) to the twig.', 'on'), (32, 'phytoremediation', 'phytoremediation', 'The use of trees to take up chemicals, binding some of the material in an inert form with the tree, and converting some of it to other substances, possibly even breaking it down into the normal end product of a tree\'s chemical processes.', 'on'), (33, 'pinnate', 'pinnate', 'Blades of lobes or veins of the leaf arranged like vanes of a feather.', 'on'), (34, 'pistil', 'pistil', 'The seed-bearing organ of the flower. The pistil consists of an ovary, stigma, and style when present.', 'on'), (35, 'pollination', 'pollination', 'To transfer pollen from the anther of a stamen to the stigma of a pistil, resulting in fertilization. This can occur either on a single plant (self-pollination) or between different plants. Insect pollination and wind pollination are two examples of natural pollination.', 'on'), (36, 'reforestation', 'reforestation', 'The planting of forested land that has been lost due to fire, logging, drought, pests, or disease to restore beauty to the landscape, provide food and habitat for wildlife, and for recreational activities.', 'on'), (37, 'riparian zone', 'riparian zone', 'an area of ecological transition between the aquatic zone and the upland zone.', 'on'), (38, 'rootstock', 'rootstock', 'The root upon which the scion is grafted.', 'on'), (39, 'samara', 'samara', 'Winged fruit.', 'on'), (40, 'scion', 'scion', 'The part of the tree that is grafted or budded to rootstock.', 'on'), (41, 'self-fertile / self-pollinating', 'self-fertile---self-pollinating', 'Fertile by means of its own pollen; this makes it theoretically possible for both pollen and ovules to unite and produce fruit without a second tree being present.', 'on'), (42, 'simple leaf', 'simple-leaf', 'A single leaf blade with a bud at the base of the leafstem.', 'on'), (43, 'sinus', 'sinus', 'Indentation between lobes on a leaf.', 'on'), (44, 'specimen tree', 'specimen-tree', 'A tree placed so people can gain the greatest enjoyment for the color, texture, scent, or other pleasures it provides.', 'on'), (45, 'spurs', 'spurs', 'Stubby, often sharp twigs.', 'on'), (46, 'teeth', 'teeth', 'Notches on the outer edge of a leaf.', 'on'), (47, 'triploid', 'triploid', 'Having three sets of chromosomes rather than the usual two. As a result, the pollen is sterile.', 'on'), (48, 'xeriscape', 'xeriscape', 'Saving water while maintaining trees and other plants in the landscape.', 'on');